// src/components/LandingPage.js

import React, { useState } from 'react';
import '../index.css';
import LoginModal from './modals/LoginModal';
import SignupModal from './modals/SignupModal';

export default function LandingPage() {
  const [isLoginOpen, setLoginOpen] = useState(false);
  const [isSignupOpen, setSignupOpen] = useState(false);

  const handleLoginClick = () => {
    setLoginOpen(true);
  }

  const handleSignupClick = () => {
    setSignupOpen(true);
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-indigo-900 flex items-center justify-center p-4">
      <div className="absolute top-0 left-0 w-full h-full opacity-10">
        {/* Your existing SVG icons here */}
      </div>
      <div className="w-full max-w-4xl flex flex-col md:flex-row items-center justify-between bg-white bg-opacity-10 backdrop-blur-lg rounded-xl shadow-2xl overflow-hidden">
        <div className="w-full md:w-1/2 p-8">
          <h1 className="text-4xl font-bold text-white mb-6">Welcome to Tele-Intelli</h1>
          <p className="text-lg text-blue-100 mb-8">
            Enhance your Telegram experience with our advanced drug detection technology. Stay safe and informed.
          </p>
          <div className="space-y-4">
            <button
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg transition duration-300 ease-in-out transform hover:scale-105"
              onClick={handleLoginClick}
            >
              Login
            </button>
            <button
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg transition duration-300 ease-in-out transform hover:scale-105"
              onClick={handleSignupClick}
            >
              Sign Up
            </button>
          </div>
        </div>
        <div className="w-full md:w-1/2 p-8 flex items-center justify-center">
          {/* Your existing shield icon and animation */}
        </div>
      </div>

      {/* Modals for Login and Signup */}
      <LoginModal isOpen={isLoginOpen} onClose={() => setLoginOpen(false)} />
      <SignupModal isOpen={isSignupOpen} onClose={() => setSignupOpen(false)} />
    </div>
  );
}
