import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import KeywordChart from './KeywordChart';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groups, setGroups] = useState([]);
  const [groupDetails, setGroupDetails] = useState(null);
  const [loadingGroups, setLoadingGroups] = useState(true);
  const [loadingDetails, setLoadingDetails] = useState(false);
  const [keywordData, setKeywordData] = useState([]);
  const [userCountData, setUserCountData] = useState([]);
  const [error, setError] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isRemoveModalOpen, setIsRemoveModalOpen] = useState(false);
  const [newKeyword, setNewKeyword] = useState('');
  const [keywordToRemove, setKeywordToRemove] = useState('');

  const memoizedGroups = useMemo(() => groups, [groups]);

  // Fetch user's groups
  useEffect(() => {
    const fetchGroups = async () => {
      if (!user || !user.id) {
        setError('User ID is not available');
        setLoadingGroups(false);
        return;
      }

      try {
        const response = await axios.get(`http://localhost:7777/groups?userId=${user.id}`);
        setGroups(response.data);
      } catch (error) {
        console.error('Error fetching groups:', error);
        setError('Error fetching groups');
      } finally {
        setLoadingGroups(false);
      }
    };
    fetchGroups();
  }, [user]);

  // Fetch group details when a group is selected
  useEffect(() => {
    if (selectedGroup) {
      setLoadingDetails(true);
      const fetchGroupDetails = async () => {
        try {
          const response = await axios.get(`http://localhost:7777/gdetails?id=${selectedGroup.group_id}`);
          setGroupDetails(response.data);

          // Fetch keyword data
          const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
            params: { groupId: selectedGroup.group_id, userId: user.id }
          });
          setKeywordData(keywordResponse.data);

          // Fetch user count data
          const userCountResponse = await axios.get(`http://localhost:7777/userCounts?groupId=${selectedGroup.id}`);
          setUserCountData(userCountResponse.data);
        } catch (error) {
          console.error('Error fetching group details:', error);
          setError('Error fetching group details');
        } finally {
          setLoadingDetails(false);
        }
      };
      fetchGroupDetails();
    }
  }, [selectedGroup, user.id]);

  // Add keyword function
  const handleAddKeyword = async () => {
    if (!newKeyword.trim() || !selectedGroup) return;

    try {
      const response = await axios.post(`http://localhost:7777/addKeyword`, {
        groupId: selectedGroup.id,
        keyword: newKeyword.trim()
      });

      setNewKeyword('');
      setIsAddModalOpen(false);

      // Update groupDetails with new keywords
      setGroupDetails((prevDetails) => ({
        ...prevDetails,
        keywords: response.data.keywords
      }));

      // Update keyword data
      const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { groupId: selectedGroup.id, userId: user.id }
      });
      setKeywordData(keywordResponse.data);
    } catch (error) {
      console.error('Error adding keyword:', error);
      setError('Error adding keyword');
    }
  };

  // Remove keyword function
  const handleRemoveKeyword = async () => {
    if (!keywordToRemove || !selectedGroup) return;

    try {
      const response = await axios.post(`http://localhost:7777/removeKeyword`, {
        groupId: selectedGroup.id,
        keyword: keywordToRemove
      });

      setIsRemoveModalOpen(false);
      setKeywordToRemove('');

      // Update groupDetails by removing the keyword
      setGroupDetails((prevDetails) => ({
        ...prevDetails,
        keywords: response.data.keywords // Updated keywords array
      }));

      // Update keyword data
      const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { groupId: selectedGroup.id, userId: user.id }
      });
      setKeywordData(keywordResponse.data);
    } catch (error) {
      console.error('Error removing keyword:', error);
      setError('Error removing keyword');
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar with Groups List */}
      <div className="w-1/4 bg-gray-800 p-4">
        <h3 className="text-white text-lg font-bold">Your Groups</h3>
        {loadingGroups ? (
          <p className="text-gray-300">Loading groups...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <ul className="space-y-2 mt-4">
            {memoizedGroups.map((group) => (
              <li
                key={group.id}
                onClick={() => setSelectedGroup(group)}
                className={`cursor-pointer p-2 rounded-lg ${selectedGroup?.id === group.id ? 'bg-blue-500 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                {group.group_name}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Main content */}
      <div className="w-3/4 bg-white p-4">
        <div className="flex justify-between items-center mb-6">
          <div className="space-x-4">
            <button className="bg-blue-500 text-white px-4 py-2 rounded">Alert System</button>
            <button className="bg-green-500 text-white px-4 py-2 rounded">Invite Bot</button>
            <button className="bg-red-500 text-white px-4 py-2 rounded">Remove Bot</button>
            <button className="bg-purple-500 text-white px-4 py-2 rounded">Sentiment Analysis</button>
          </div>
          <div className="text-gray-800">
            Welcome, <span className="font-bold">{user?.name || 'User'}</span>
            <button onClick={logout} className="ml-4 text-red-500">Logout</button>
          </div>
        </div>

        {/* Group-specific content */}
        {loadingDetails ? (
          <p>Loading group details...</p>
        ) : groupDetails ? (
          <div>
            <h2 className="text-2xl font-bold mb-4">{groupDetails.group_name} - Analytics</h2>

            {/* Display Keyword Chart */}
            <KeywordChart groupId={selectedGroup.group_id} userId={user.id} /> {/* Pass groupId and userId */}

            <div className="mt-8">
              <div className="flex space-x-6">
                {/* Keyword and Count Table */}
                <div className="w-1/2">
                  <h3 className="text-lg font-bold text-center mb-4">Keywords and Counts</h3>
                  <table className="table-auto w-full mb-6">
                    <thead>
                      <tr>
                        <th className="px-4 py-2 text-center">Keyword</th>
                        <th className="px-4 py-2 text-center">Count</th>
                      </tr>
                    </thead>
                    <tbody>
                      {keywordData.map((item, index) => (
                        <tr key={index}>
                          <td className="border px-4 py-2 text-center">{item.keyword}</td>
                          <td className="border px-4 py-2 text-center">{item.count}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>

                  {/* Add/Remove Keywords Buttons */}
                  <div className="flex justify-center space-x-4 mb-6">
                    <button 
                      onClick={() => setIsAddModalOpen(true)} 
                      className="bg-green-500 text-white px-4 py-2 rounded"
                    >
                      Add Keyword
                    </button>
                    <button 
                      onClick={() => setIsRemoveModalOpen(true)} 
                      className="bg-red-500 text-white px-4 py-2 rounded"
                    >
                      Remove Keyword
                    </button>
                  </div>
                </div>

                {/* Users and Count Table */}
                <div className="w-1/2">
                  <h3 className="text-lg font-bold text-center mb-4">Users and Counts</h3>
                  <table className="table-auto w-full mb-6">
                    <thead>
                      <tr>
                        <th className="px-4 py-2 text-center">User</th>
                        <th className="px-4 py-2 text-center">Count</th>
                      </tr>
                    </thead>
                    <tbody>
                      {userCountData.map((item, index) => (
                        <tr key={index}>
                          <td className="border px-4 py-2 text-center">{item.user}</td>
                          <td className="border px-4 py-2 text-center">{item.count}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <p className="text-gray-500">Select a group to view analytics.</p>
        )}
      </div>

      {/* Add Keyword Modal */}
      {isAddModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Add Keyword</h2>
            <input 
              type="text" 
              value={newKeyword} 
              onChange={(e) => setNewKeyword(e.target.value)} 
              placeholder="Enter new keyword"
              className="border p-2 w-full mb-4"
            />
            <div className="flex justify-end space-x-4">
              <button onClick={() => setIsAddModalOpen(false)} className="bg-gray-300 px-4 py-2 rounded">Cancel</button>
              <button onClick={handleAddKeyword} className="bg-green-500 text-white px-4 py-2 rounded">Add</button>
            </div>
          </div>
        </div>
      )}

      {/* Remove Keyword Modal */}
      {isRemoveModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Remove Keyword</h2>
            <select 
              value={keywordToRemove} 
              onChange={(e) => setKeywordToRemove(e.target.value)} 
              className="border p-2 w-full mb-4"
            >
              <option value="">Select keyword to remove</option>
              {groupDetails?.keywords?.map((keyword, index) => (
                <option key={index} value={keyword}>{keyword}</option>
              ))}
            </select>
            <div className="flex justify-end space-x-4">
              <button onClick={() => setIsRemoveModalOpen(false)} className="bg-gray-300 px-4 py-2 rounded">Cancel</button>
              <button onClick={handleRemoveKeyword} className="bg-red-500 text-white px-4 py-2 rounded">Remove</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
