// KeywordChart.js
import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';

const KeywordChart = ({ groupId }) => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchKeywordData = async () => {
      const response = await axios.get(`/api/groups/${groupId}/keyword-data`); // Adjust API endpoint
      const data = response.data;

      const labels = data.map(item => item.keyword);
      const usageCounts = data.map(item => item.count);

      setChartData({
        labels,
        datasets: [{
          label: 'Keyword Usage',
          data: usageCounts,
          backgroundColor: 'rgba(54, 162, 235, 0.6)'
        }]
      });
    };

    fetchKeywordData();
  }, [groupId]);

  if (!chartData) return <p>Loading chart...</p>;

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Keyword Usage Chart</h3>
      <Bar data={chartData} />
    </div>
  );
};

export default KeywordChart;
