import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import KeywordChart from './KeywordChart';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groups, setGroups] = useState([]);
  const [groupDetails, setGroupDetails] = useState(null);
  const [loadingGroups, setLoadingGroups] = useState(true);
  const [loadingDetails, setLoadingDetails] = useState(false);
  const [keywordData, setKeywordData] = useState([]);
  const [userCountData, setUserCountData] = useState([]);
  const [error, setError] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isRemoveModalOpen, setIsRemoveModalOpen] = useState(false);
  const [newKeyword, setNewKeyword] = useState('');
  const [selectedKeywordToRemove, setSelectedKeywordToRemove] = useState('');

  // Fetch user's groups
  useEffect(() => {
    const fetchGroups = async () => {
      if (!user || !user.id) {
        setError('User ID is not available');
        setLoadingGroups(false);
        return;
      }

      try {
        const response = await axios.get(`http://localhost:7777/groups?userId=${user.id}`);
        setGroups(response.data);
      } catch (error) {
        console.error('Error fetching groups:', error);
        setError('Error fetching groups');
      } finally {
        setLoadingGroups(false);
      }
    };
    fetchGroups();
  }, [user]);

  // Fetch group details when a group is selected
  useEffect(() => {
    if (selectedGroup) {
      setLoadingDetails(true);
      const fetchGroupDetails = async () => {
        try {
          const response = await axios.get(`http://localhost:7777/gdetails?id=${selectedGroup.group_id}`);
          setGroupDetails(response.data);

          // Fetch keyword data
          const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
            params: { groupId: selectedGroup.group_id, userId: user.id }
          });
          setKeywordData(keywordResponse.data);

          // Fetch user count data
          const userCountResponse = await axios.get(`http://localhost:7777/userCounts?groupId=${selectedGroup.id}`);
          setUserCountData(userCountResponse.data);
        } catch (error) {
          console.error('Error fetching group details:', error);
          setError('Error fetching group details');
        } finally {
          setLoadingDetails(false);
        }
      };
      fetchGroupDetails();
    }
  }, [selectedGroup, user.id]);

  const handleAddKeyword = async () => {
    if (!newKeyword.trim() || !selectedGroup) return;

    try {
      const response = await axios.post(`http://localhost:7777/addKeyword`, {
        groupId: selectedGroup.id,
        keyword: newKeyword.trim()
      });

      setNewKeyword('');
      setIsAddModalOpen(false);

      setGroupDetails(prevDetails => ({
        ...prevDetails,
        keywords: response.data.keywords
      }));

      const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { groupId: selectedGroup.id, userId: user.id }
      });
      setKeywordData(keywordResponse.data);

    } catch (error) {
      console.error('Error adding keyword:', error);
      setError('Error adding keyword');
    }
  };

  const handleRemoveKeyword = async () => {
    if (!selectedKeywordToRemove || !selectedGroup) return;

    try {
      const response = await axios.post(`http://localhost:7777/removeKeyword`, {
        groupId: selectedGroup.id,
        keyword: selectedKeywordToRemove
      });

      setSelectedKeywordToRemove('');
      setIsRemoveModalOpen(false);

      setGroupDetails(prevDetails => ({
        ...prevDetails,
        keywords: response.data.keywords
      }));

      const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { groupId: selectedGroup.id, userId: user.id }
      });
      setKeywordData(keywordResponse.data);

    } catch (error) {
      console.error('Error removing keyword:', error);
      setError('Error removing keyword');
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-1/4 bg-gray-200">
        {loadingGroups ? (
          <p>Loading groups...</p>
        ) : (
          <ul>
            {groups.length > 0 ? (
              groups.map((group) => (
                <li
                  key={group.id}
                  className={`p-2 cursor-pointer ${selectedGroup === group ? 'bg-blue-300' : ''}`}
                  onClick={() => setSelectedGroup(group)}
                >
                  {group.group_name}
                </li>
              ))
            ) : (
              <p>No groups available</p>
            )}
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div className="flex-1 p-6">
        {loadingDetails ? (
          <p>Loading group details...</p>
        ) : selectedGroup && groupDetails ? (
          <>
            {/* Keyword Chart */}
            <KeywordChart data={keywordData} />

            {/* Tables for Keywords and User Counts */}
            <div className="flex justify-around mt-6">
              <div>
                <h3 className="text-xl font-semibold mb-2">Keywords</h3>
                <table className="min-w-full bg-white shadow-md rounded">
                  <thead>
                    <tr>
                      <th className="py-2 px-4 bg-gray-100 border-b">Keyword</th>
                      <th className="py-2 px-4 bg-gray-100 border-b">Count</th>
                    </tr>
                  </thead>
                  <tbody>
                    {keywordData.map((keyword, index) => (
                      <tr key={index}>
                        <td className="py-2 px-4 border-b">{keyword.keyword}</td>
                        <td className="py-2 px-4 border-b">{keyword.count}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              <div>
                <h3 className="text-xl font-semibold mb-2">Users</h3>
                <table className="min-w-full bg-white shadow-md rounded">
                  <thead>
                    <tr>
                      <th className="py-2 px-4 bg-gray-100 border-b">User</th>
                      <th className="py-2 px-4 bg-gray-100 border-b">Count</th>
                    </tr>
                  </thead>
                  <tbody>
                    {userCountData.map((user, index) => (
                      <tr key={index}>
                        <td className="py-2 px-4 border-b">{user.username}</td>
                        <td className="py-2 px-4 border-b">{user.count}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Add/Remove Keyword Buttons */}
            <div className="flex justify-center space-x-4 mt-6">
              <button onClick={() => setIsAddModalOpen(true)} className="bg-green-500 text-white px-4 py-2 rounded">
                Add Keyword
              </button>
              <button onClick={() => setIsRemoveModalOpen(true)} className="bg-red-500 text-white px-4 py-2 rounded">
                Remove Keyword
              </button>
            </div>

            {/* Modals for adding and removing keywords */}
            {isAddModalOpen && (
              <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
                <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
                  <h3 className="text-lg font-bold mb-4">Add New Keyword</h3>
                  <input
                    type="text"
                    value={newKeyword}
                    onChange={(e) => setNewKeyword(e.target.value)}
                    className="w-full px-3 py-2 border rounded-md"
                    placeholder="Enter new keyword"
                  />
                  <div className="flex justify-end mt-4">
                    <button
                      onClick={() => setIsAddModalOpen(false)}
                      className="px-4 py-2 bg-gray-300 text-gray-800 rounded-md mr-2"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handleAddKeyword}
                      className="px-4 py-2 bg-blue-500 text-white rounded-md"
                    >
                      Add
                    </button>
                  </div>
                </div>
              </div>
            )}

            {isRemoveModalOpen && (
              <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
                <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
                  <h3 className="text-lg font-bold mb-4">Remove Keyword</h3>
                  <select
                    value={selectedKeywordToRemove}
                    onChange={(e) => setSelectedKeywordToRemove(e.target.value)}
                    className="w-full px-3 py-2 border rounded-md"
                  >
                    <option value="">Select keyword to remove</option>
                    {groupDetails.keywords.map((keyword, index) => (
                      <option key={index} value={keyword}>
                        {keyword}
                      </option>
                    ))}
                  </select>
                  <div className="flex justify-end mt-4">
                    <button
                      onClick={() => setIsRemoveModalOpen(false)}
                      className="px-4 py-2 bg-gray-300 text-gray-800 rounded-md mr-2"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handleRemoveKeyword}
                      className="px-4 py-2 bg-red-500 text-white rounded-md"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              </div>
            )}
          </>
        ) : (
          <p>Select a group to view analytics</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
