import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,     // Import category scale
  LinearScale,      // Import linear scale
  BarElement,       // Import bar element
  Title,            // Optional, if you want to use a chart title
  Tooltip,
  Legend
} from 'chart.js';

// Register the components that are required
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const KeywordChart = ({ groupId, userId }) => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchKeywordData = async () => {
      const response = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { userId , groupId}
      });
      const data = response.data;




      const labels = data.map(item => item.keyword);
      const usageCounts = data.map(item => item.count);

      setChartData({
        labels,
        datasets: [{
          label: 'Keyword Usage',
          data: usageCounts,
          backgroundColor: 'rgba(54, 162, 235, 0.6)'
        }]
      });
    };

    fetchKeywordData();
  }, [groupId, userId]);

  if (!chartData) return <p>Loading chart...</p>;

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Keyword Usage Chart</h3>
      <Bar data={chartData} />
    </div>
  );
};

export default KeywordChart;
