const EnhancedFeatures = () => {
  const features = [
      { icon: Globe, title: "Enhanced Integration", description: "Advanced Security Capabilities" },
      { icon: Shield, title: "Real-time Monitoring", description: "Dynamic 3D Visualization with Real time insights" },
      { icon: Phone, title: "Intelligent Alerts", description: "Stay one step ahead with our intelligent alert system" },
      { icon: Copy, title: "Easy Oversight", description: "Manage multiple groups with ease." }
  ];

  return (
      <div className="min-h-screen flex flex-col">
          <section className="w-full py-20 bg-gradient-to-b from-gray-100 to-white flex-grow">
              <div className="max-w-6xl mx-auto px-4">
                  <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8 }} className="text-center mb-12">
                      <motion.h2 initial={{ y: 20 }} animate={{ y: 0 }} className="text-4xl font-bold text-gray-800 mb-6">
                          Why{" "}
                          <span className="bg-gradient-to-r from-blue-500 to-cyan-500 text-transparent bg-clip-text"> Tele-Intelli </span>?
                      </motion.h2>
                  </motion.div>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-1"> {/* Change gap here */}
                      {features.map((feature, index) => (
                          <FeatureCard key={index} icon={feature.icon} title={feature.title} description={feature.description} />
                      ))}
                  </div>
              </div>
          </section>
          <footer className="text-left text-gray-500 p-2 bg-white border-t w-full"> {/* Reduced padding */}
              Brought to you by NerdyEther
          </footer>
      </div>
  );
};
