import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import KeywordChart from './KeywordChart';
import InviteBotModal from './InviteBotModal'; // Import InviteBotModal

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groups, setGroups] = useState([]);
  const [groupDetails, setGroupDetails] = useState(null);
  const [loadingGroups, setLoadingGroups] = useState(true);
  const [loadingDetails, setLoadingDetails] = useState(false);
  const [keywordData, setKeywordData] = useState([]);
  const [userCountData, setUserCountData] = useState([]);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newKeyword, setNewKeyword] = useState('');
  const [removeModalOpen, setRemoveModalOpen] = useState(false);
  const [keywordsToRemove, setKeywordsToRemove] = useState([]);
  const [selectedKeyword, setSelectedKeyword] = useState('');
  const [totalMessages, setTotalMessages] = useState(0);
  const [inviteModalOpen, setInviteModalOpen] = useState(false); // State for InviteBotModal

  const memoizedGroups = useMemo(() => groups, [groups]);

  useEffect(() => {
    const fetchTotalMessages = async () => {
      if (!selectedGroup || !user) return;
      try {
        const response = await axios.get('http://localhost:7777/totalMessages', {
          params: { groupId: selectedGroup.group_id, userId: user.id }
        });
        setTotalMessages(response.data.totalMessages);
      } catch (error) {
        console.error("Error fetching total messages:", error);
      }
    };
    fetchTotalMessages();
  }, [selectedGroup, user]);

  useEffect(() => {
    const fetchGroups = async () => {
      if (!user || !user.id) {
        setError('User ID is not available');
        setLoadingGroups(false);
        return;
      }
      try {
        const response = await axios.get(`http://localhost:7777/groups?userId=${user.id}`);
        setGroups(response.data);
      } catch (error) {
        console.error('Error fetching groups:', error);
        setError('Error fetching groups');
      } finally {
        setLoadingGroups(false);
      }
    };
    fetchGroups();
  }, [user]);

  useEffect(() => {
    const fetchGroupDetails = async () => {
      if (!selectedGroup) return;
      setLoadingDetails(true);
      try {
        const response = await axios.get(`http://localhost:7777/gdetails?id=${selectedGroup.id}`);
        setGroupDetails(response.data);
        setKeywordsToRemove(response.data.keywords || []);

        const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
          params: { groupId: selectedGroup.group_id, userId: user.id }
        });
        setKeywordData(keywordResponse.data);

        const userCountResponse = await axios.get(`http://localhost:7777/userCounts?groupId=${selectedGroup.group_id}`);
        setUserCountData(userCountResponse.data);
      } catch (error) {
        console.error('Error fetching group details:', error);
        setError('Error fetching group details');
      } finally {
        setLoadingDetails(false);
      }
    };
    fetchGroupDetails();
  }, [selectedGroup, user.id]);

  const handleAddKeyword = async () => {
    if (!newKeyword.trim()) {
      setError("Keyword cannot be empty");
      return;
    }
    try {
      await axios.post(`http://localhost:7777/addKeyword`, {
        groupId: selectedGroup.group_id,
        keyword: newKeyword.trim(),
      });
      const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { groupId: selectedGroup.group_id, userId: user.id },
      });
      setKeywordData(keywordResponse.data);
      setNewKeyword('');
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error adding keyword:', error);
      setError('Error adding keyword');
    }
  };

  const handleRemoveKeyword = async () => {
    if (!selectedKeyword) {
      setError("No keyword selected");
      return;
    }
    try {
      await axios.post(`http://localhost:7777/removeKeyword`, {
        groupId: selectedGroup.group_id,
        keyword: selectedKeyword,
      });
      const keywordResponse = await axios.get(`http://localhost:7777/keywordchart`, {
        params: { groupId: selectedGroup.group_id, userId: user.id },
      });
      setKeywordData(keywordResponse.data);
      setSelectedKeyword('');
      setRemoveModalOpen(false);
    } catch (error) {
      console.error('Error removing keyword:', error);
      setError('Error removing keyword');
    }
  };

  const handleInvite = async (groupId) => {
    try {
      await axios.post('http://localhost:7777/inviteBot', {
        groupId,
        userId: user.id, // Pass userId from the context
      });
      setInviteModalOpen(false);
      // Optionally, you can trigger a refresh of groups or show a success message here
    } catch (error) {
      console.error('Error inviting bot:', error);
      setError('Error inviting bot');
    }
  };

  return (
    <div className="flex flex-col h-screen">
      {/* Fixed Top Navigation Bar */}
      <div className="bg-gray-800 text-white p-4 flex justify-between items-center">
        <div className="space-x-4">
          <button onClick={() => setInviteModalOpen(true)} className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">Invite Bot</button>
          <button className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">Remove Bot</button>
        </div>
        <div>
          Welcome, <span className="font-bold">{user?.name || 'User'}</span>
          <button onClick={logout} className="ml-4 text-red-500 hover:text-red-600">Logout</button>
        </div>
      </div>

      {/* Main content area */}
      <div className="flex flex-1 overflow-hidden">
        {/* Fixed Sidebar with Groups List */}
        <div className="w-1/4 bg-gray-800 p-4 overflow-y-auto">
          <h3 className="text-white text-lg font-bold mb-4">Your Groups</h3>
          {loadingGroups ? (
            <p className="text-gray-300">Loading groups...</p>
          ) : error ? (
            <p className="text-red-500">{error}</p>
          ) : (
            <ul className="space-y-2">
              {memoizedGroups.map((group) => (
                <li
                  key={group.id}
                  onClick={() => setSelectedGroup(group)}
                  className={`cursor-pointer p-2 rounded-lg ${
                    selectedGroup?.id === group.id
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                  }`}
                >
                  {group.group_name}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Main content area with scrolling */}
        <div className="flex-1 overflow-y-auto bg-white p-4">
          {/* Group-specific content */}
          {loadingDetails ? (
            <p>Loading group details...</p>
          ) : groupDetails ? (
            <div>
              <h2 className="text-2xl font-bold mb-4">{groupDetails.group_name} - Analytics</h2>
              <KeywordChart groupId={selectedGroup.group_id} userId={user.id} />

              <div className="mt-8">
                <div className="flex space-x-6">
                  {/* Keyword and Count Table */}
                  <div className="w-1/2">
                    <h3 className="text-lg font-bold text-center mb-4">Keywords and Counts</h3>
                    <table className="table-auto w-full mb-6">
                      <thead>
                        <tr>
                          <th className="px-4 py-2 text-center">Keyword</th>
                          <th className="px-4 py-2 text-center">Count</th>
                        </tr>
                      </thead>
                      <tbody>
                        {keywordData.map((item, index) => (
                          <tr key={index}>
                            <td className="border px-4 py-2 text-center">{item.keyword}</td>
                            <td className="border px-4 py-2 text-center">{item.count}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>

                    {/* Add/Remove Keywords Buttons */}
                    <div className="flex justify-between mt-4">
                      <button onClick={() => setIsModalOpen(true)} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Add Keyword</button>
                      <button onClick={() => setRemoveModalOpen(true)} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">Remove Keyword</button>
                    </div>
                  </div>

                  {/* User Count Table */}
                  <div className="w-1/2">
                    <h3 className="text-lg font-bold text-center mb-4">User Count</h3>
                    <table className="table-auto w-full mb-6">
                      <thead>
                        <tr>
                          <th className="px-4 py-2 text-center">User</th>
                          <th className="px-4 py-2 text-center">Count</th>
                        </tr>
                      </thead>
                      <tbody>
                        {userCountData.map((item, index) => (
                          <tr key={index}>
                            <td className="border px-4 py-2 text-center">{item.sender_name}</td>
                            <td className="border px-4 py-2 text-center">{item.count}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
                <p>Total Messages: {totalMessages}</p>
              </div>
            </div>
          ) : (
            <p className="text-gray-500">Select a group to view analytics.</p>
          )}
        </div>
      </div>

      {/* InviteBotModal for inviting the bot */}
      <InviteBotModal 
        isOpen={inviteModalOpen} 
        onClose={() => setInviteModalOpen(false)} 
        onInvite={handleInvite} 
      />

      {/* Add Keyword Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl mb-4">Add Keyword</h2>
            <input
              type="text"
              value={newKeyword}
              onChange={(e) => setNewKeyword(e.target.value)}
              className="border px-4 py-2 w-full mb-4"
              placeholder="Enter keyword"
            />
            <div className="flex justify-between">
              <button onClick={handleAddKeyword} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Add</button>
              <button onClick={() => setIsModalOpen(false)} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Cancel</button>
            </div>
            {error && <p className="text-red-500 mt-2">{error}</p>}
          </div>
        </div>
      )}

      {/* Remove Keyword Modal */}
      {removeModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl mb-4">Remove Keyword</h2>
            <select value={selectedKeyword} onChange={(e) => setSelectedKeyword(e.target.value)} className="border px-4 py-2 w-full mb-4">
              <option value="">Select a keyword to remove</option>
              {keywordsToRemove.map((keyword, index) => (
                <option key={index} value={keyword}>{keyword}</option>
              ))}
            </select>
            <div className="flex justify-between">
              <button onClick={handleRemoveKeyword} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">Remove</button>
              <button onClick={() => setRemoveModalOpen(false)} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Cancel</button>
            </div>
            {error && <p className="text-red-500 mt-2">{error}</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
