import * as React from 'react';

function call(ref, value) {
}
function useEffectfulState(fn, deps = [], cb) {
  const [state, set] = React.useState();
  React.useLayoutEffect(() => {
    const value = fn();
    set(value);
    return () => call();
  }, deps);
  return state;
}

export { useEffectfulState };
