import React, { createContext, useContext, useState, useEffect } from 'react';

// Create the AuthContext
const AuthContext = createContext();

// Custom hook to use the AuthContext
export function useAuth() {
  return useContext(AuthContext);
}

// AuthProvider component to wrap around the app
export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);

  // Check local storage for token on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
        console.lo
      // You may want to decode the token to get user info, but for now, we just check its existence
      setUser({ token }); // or setUser(decodeToken(token)) if you have a function to decode it
    }
  }, []);

  // Context value contains user and setUser
  const value = {
    user,
    setUser,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}
