import React, { useState } from 'react';
import '../index.css';
import LoginModal from './modals/LoginModal';
import SignupModal from './modals/SignupModal';
// Import Shadcn Alert component
import { Alert, AlertTitle, AlertDescription } from "/components/ui/alert";

export default function LandingPage() {
    const [isLoginOpen, setLoginOpen] = useState(false);
    const [isSignupOpen, setSignupOpen] = useState(false);

    const handleLoginClick = () => {
        setLoginOpen(true);
    }

    const handleSignupClick = () => {
        setSignupOpen(true);
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-900 to-indigo-900 flex items-center justify-center p-4">
            {/* Shadcn Alert component */}
            <Alert className="absolute top-4 left-1/2 transform -translate-x-1/2 max-w-md bg-red-500 text-white">
                <AlertTitle>Notice</AlertTitle>
                <AlertDescription>
                    This is a Shadcn alert. Check if this component appears correctly!
                </AlertDescription>
            </Alert>

            <div className="absolute top-0 left-0 w-full h-full opacity-10">
                <div className="absolute top-10 left-10 animate-pulse">
                    {/* Message Circle Icon */}
                    <svg xmlns="http://www.w3.org/2000/svg" width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                        <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
                    </svg>
                </div>
                {/* ... Rest of your code */}
            </div>

            {/* Rest of your component */}
            <LoginModal isOpen={isLoginOpen} onClose={() => setLoginOpen(false)} />
            <SignupModal isOpen={isSignupOpen} onClose={() => setSignupOpen(false)} />
        </div>
    )
}
