import React, { useState } from 'react';

const InviteBotModal = ({ isOpen, onClose, onInvite }) => {
  const [groupId, setGroupId] = useState('');

  const handleInvite = () => {
    if (groupId.trim()) {
      onInvite(groupId.trim());
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded shadow-lg">
        <h2 className="text-xl mb-4">Invite Bot to Group</h2>
        <input
          type="text"
          value={groupId}
          onChange={(e) => setGroupId(e.target.value)}
          className="border px-4 py-2 w-full mb-4"
          placeholder="Enter group Telegram ID"
        />
        <div className="flex justify-between">
          <button onClick={handleInvite} className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">Invite</button>
          <button onClick={onClose} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default InviteBotModal;
